package mypractice.basicexamples.operators;

public class TernaryTest {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int v = 1;
	    System.out.println(v == 1 ? "A" : "B");

	    v++;
	    System.out.println(v == 1 ? "A" : "B");
	}

}
